import React from "react";


function drawPoint(x, y, context, color) {
    context.save();
    context.fillStyle = {color};
    context.beginPath();
    context.arc(x, y, 3, 0, 2 * Math.PI, false);
    context.fill();
    context.restore();
  }

const updateRadar = ({droneData}) => {
    droneData = droneData.filter(Boolean)
    var c = document.getElementById("radarcanvas");
    var context = c.getContext("2d");
    const p = 0;
    console.log('here')
    Object.keys(droneData).map(drone => {
        console.log(droneData[drone].x/1000)
        drawPoint(droneData[drone].x/1000, droneData[drone].y/1000, context, '#0000FF')
    })
    drawPoint(250, 250, '#0000FF')
};

const setupRadar = () => {
    var c = document.getElementById("radarcanvas");
    var context = c.getContext("2d");
    context.clearRect(0, 0, c.width, c.height);
    const p = 0;
    for (var x = 0; x <= c.width; x += 25) {
        context.moveTo(0.5 + x + p, p);
        context.lineTo(0.5 + x + p, c.height + p);
    }
    for (var x = 0; x <= c.height; x += 25) {
        context.moveTo(p, 0.5 + x + p);
        context.lineTo(c.width + p, 0.5 + x + p);
    }
    context.strokeStyle = "black";
    context.stroke()
}

export default {
    updateRadar: updateRadar,
    setupRadar: setupRadar
};
